// import logo from './logo.svg';
import './App.css';
import {useState} from 'react'

const articleList = [
  // html
  { title: 'HTML', body: 'HTML is Hyper-Text Markup Language'},

  // css
  { title: 'CSS', body: 'CSS defines the style of page' },

  // js
  { title: 'JS', body: 'Javascript defines the action of page' }
];

function HeaderTag() {
  return (
    <header>
      <h1>React Sample Page</h1>
    </header>
  )
}

function Nav(props) {
  const clickHandler = (e) => {
    e.preventDefault()
    props.setPostId(Number(props.postId))
  }
  return (
    <nav>
      <ol>
        <li><a postId='0' onClick={clickHandler} href='#'>html</a></li>
        <li><a postId='1' onClick={clickHandler} href='#'>css</a></li>
        <li><a postId='2' onClick={clickHandler} href='#'>js</a></li>
      </ol>
    </nav>
  )
}

function Article(props) {
  return (
  <article>
    <h2>{articleList[props.postId].title}</h2>
    {articleList[props.postId].body}
  </article>)
}


function App() {
  const [id, setId] = useState(0)
  return (
    <>
    <HeaderTag />
    <Nav setPostId={setId} />
    <Article postId={id}/>
    </>
    );
}

export default App;
